/*Table structure for table `mall_coupon_group` */

DROP TABLE IF EXISTS `mall_coupon_group`;

CREATE TABLE `mall_coupon_group` (
  `id` int unsigned NOT NULL AUTO_INCREMENT COMMENT '优惠码组id',
  `name` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '优惠码名称',
  `coupon_type` int NOT NULL COMMENT '类型:1金额优惠,2折扣优惠',
  `discount` int unsigned NOT NULL COMMENT '优惠码折扣(百分数)/优惠码金额(乘以100)',
  `count_num` int unsigned NOT NULL COMMENT '发放数量',
  `created_num` int unsigned NOT NULL COMMENT '已生成数量',
  `used_num` int unsigned NOT NULL COMMENT '已使用的数量',
  `expiration_start` int unsigned NOT NULL COMMENT '开始有效日期',
  `expiration_end` int unsigned NOT NULL COMMENT '结束有效日期',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '上架1，下架0',
  `create_uid` int unsigned NOT NULL COMMENT '创建人',
  `descr` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '描述',
  `category_id` varchar(250) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '配套产品，分类id',
  `teaching_method` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '授课方式 1.面授 2.直播 3.点播 4.面授+直播 5.直播+点播 6.点播+面授 7.直播+点播+面授',
  `goods_ids` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT '商品id,以'',''隔开',
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL,
  `deleted_at` datetime DEFAULT NULL,
  `has_code` int unsigned NOT NULL COMMENT '是否生成优惠码',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

