  `course_count` int DEFAULT NULL COMMENT '课次数',
  `market_price` int DEFAULT NULL COMMENT '成本价,乘以100',
  `shop_price` int DEFAULT NULL COMMENT '售价,乘以100',
  `buy_number` int DEFAULT NULL COMMENT '购买人数',
  `buy_number_false` int DEFAULT NULL COMMENT '购买人数真假数据之和',
