  `is_group` tinyint(1) NOT NULL COMMENT '是否是组合0：否，1：是',
  `group_type` int DEFAULT NULL COMMENT '0-未知，1-可选,2-全部',
  `agreement_id` int unsigned NOT NULL COMMENT '协议id',
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL,
  `deleted_at` datetime DEFAULT NULL,
  `top_value` int DEFAULT NULL COMMENT '热门课程',
  `uuid` char(36) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '唯一码(DC2Type:guid)',
  `recommend_value` int DEFAULT NULL COMMENT '推荐课程',
  `good_type` int unsigned NOT NULL COMMENT '产品类型,1-视频课程,2-试卷',
  `parameter1` varchar(250) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '扩展参数1',
  `parameter2` varchar(250) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '扩展参数2',
  `parameter3` varchar(250) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '扩展参数3',
  PRIMARY KEY (`id`),
  UNIQUE KEY `UNIQ_79401526D17F50A6` (`uuid`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

