DROP TABLE IF EXISTS `mall_order`;

CREATE TABLE `mall_order` (
  `id` int unsigned NOT NULL AUTO_INCREMENT COMMENT '订单表id',
  `name` varchar(1000) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '订单名称',
  `order_no` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '订单号',
  `uid` int unsigned NOT NULL COMMENT '下单人',
  `goods_id` int unsigned NOT NULL COMMENT '商品id',
  `order_agreement_id` int unsigned NOT NULL COMMENT '订单协议',
  `order_amount` int NOT NULL COMMENT '订单实际支付价格，已减去优惠价格',
  `discount_amount` int NOT NULL COMMENT '优惠金额',
  `original_amount` int NOT NULL COMMENT '原价',
  `order_status` int NOT NULL COMMENT '订单状态:0支付过期,1待支付,2支付成功,3已取消',
  `payment_type` int NOT NULL COMMENT '支付方式:1支付宝,2微信支付',
  `user_notes` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '用户备注',
  `referer` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'CRM' COMMENT '订单来源',
  `coupon_sn` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '订单使用优惠码编号',
  `goods_all` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '多个goodsid',
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL,
  `deleted_at` datetime DEFAULT NULL,
  `coupon_group_id` int unsigned NOT NULL COMMENT '优惠码组',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=35 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

